import React, { useState, useEffect }  from 'react';
import { Typography, List, ListItem, ListItemText } from '@mui/material';

const url = REACT_APP_TAIGA_URL + '/api/v1';

const login_payload = {
  username: process.env.REACT_APP_TAIGA_USER,
  password: process.env.REACT_APP_TAIGA_PASS,
  type:"normal"
};

const login_options = {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify(login_payload)
}

const TaigaProjects = () => {
  const [login, setLogin] = useState({"username": "", "token": ""});
  const [projects, setProjects] = useState([]);

  const fetchLogin = async () => {
    try {
      const login = await fetch(process.env.REACT_APP_TAIGA_URL+'/api/v1'+'/auth', login_options);

      if (!login.ok) {
        throw new Error('Network response was not ok');
      }

      const login_data = await response.json();
      setLogin(login_data);
    } catch (error) {
      console.error('Error:', error);
    }
  }

  useEffect(() => {
    fetchLogin();

    fetch(process.env.REACT_APP_TAIGA_URL)

    fetch(process.env.REACT_APP_TAIGA_URL + '/api/v1' + '/projects')
      .then(response => response.json())
      .then(json => setProjects(json))
      .catch(error => console.error(error));
  }, []);

  return (
    <div style={{ marginTop: '20px' }}>
      <Typography variant="h4" gutterBottom>
        Taiga Project Management
      </Typography>
      <List>
        {projects.map((project) => (
          <ListItem key={project.id}>
            <ListItemText primary={project.name} secondary={project.description} />
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default TaigaProjects;
