import React, {useEffect, useState } from 'react';

import Heading from '../widgets/Heading';

import TaigaProjects from './TaigaProjects';

const Home = () => {
  const [config, setConfig] = useState({ datum: '', klassen: [] });
  // const [isOverflowing, setIsOverflowing] = useState(false);
  const tableRef = useRef();

  useEffect(() => {
    // Function to fetch data
    const fetchData = () => {
      if (tableRef.current && tableRef.current.offsetWidth < tableRef.current.scrollWidth) {
        // setIsOverflowing(true);
      }

      fetch("https://abi-backend.dev.manuelselch.de/"+configName)
        .then(response => response.json())
        .then(data => setConfig(data))
        .catch(error => console.error('Error fetching config:', error));
    };

    // Fetch data immediately
    fetchData();

    // Set up interval to fetch data every second
    const intervalId = setInterval(fetchData, 1000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
    <div
      className="hero bg-no-repeat bg-cover"
      style={{ 
        backgroundImage: `url("/banner.jpg")` 
      }}
    >
      <div className="hero-overlay bg-opacity-60"/>
      <h1 className="text-5xl font-bold absolute bottom-10 left-10 text-white">Manuel Selch</h1>
    </div>

    <div className='min-h-screen'>


        <TaigaProjects/>

      </div>
    </div>
  );
};

export default Home;
