import { useState, useEffect } from 'react';
import useLogin from './useLogin';
import apiService from '../services/apiService';

const useFetchProjects = () => {
    const [data, setData] = useState([]);
    const token  = useLogin();  // Destructure loading state from useLogin

    useEffect(() => {
        const fetchData = async () => {
            if (!loading && token) {  // Ensure the token is available and not loading
                const result = await apiService.get("projects");
                setData(result);
            }
        };

        fetchData();
    }, [loading, token]);  // Only re-run when loading or token changes

    return data;
};

export default useFetchProjects;
