import React, { useState, useEffect }  from 'react';
import { useParams } from 'react-router-dom'
import LocalStorage from "../hooks/LocalStorage";
import { Bar } from 'react-chartjs-2';

const url = process.env.REACT_APP_TAIGA_URL + '/api/v1';

const BarChart = () => {
  const data = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Sales',
        data: [12, 19, 3, 5, 2, 3],
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="max-w-lg mx-auto p-4 bg-white rounded-lg shadow-md">
      <Bar data={data} options={options} />
    </div>
  );
};



const TaigaProjectDetails = () => {
    const { slug } = useParams();
    const [project, setProject] = useState({});
    const [taigaToken, saveToken] = LocalStorage('taigaToken', "");

    const date = new Date(project.created_date);

    // Format the date to exclude time
    const formattedDate = date.toLocaleDateString('de-DE', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit'
    });

    useEffect(() => {
        const encodedValue = encodeURIComponent(slug);

        const project_options = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + taigaToken
            }
        }

        fetch(url + '/projects/by_slug?slug=' + encodedValue, project_options)
        .then(response => response.json())
        .then(json => setProject(json))
        .catch(error => console.error(error))

    }, []);

    return (
        <div className="p-10">
            <p className="text-4xl font-bold">{slug}</p>

            <div className="flex flex-row gap-10 pt-10">
                <BarChart />
                <div className="basis-1/2 flex flex-col gap-4">
                    <div className="skeleton h-32 w-full"></div>
                    <div className="skeleton h-4 w-28"></div>
                    <div className="skeleton h-4 w-full"></div>
                    <div className="skeleton h-4 w-full"></div>
                </div>

                <div className="basis-1/2">
                    <div className="card bg-base-100 shadow-xl">
                        <div className="card-body">
                            <h2 className="card-title">Project information</h2>
                            <hr/>

                            <p><strong>Category:</strong> App</p>

                            <p><strong>Date:</strong> {formattedDate}</p>
                        </div>
                    </div>

                    <div className="card bg-base-100 shadow-xl mt-10">
                        <div className="card-body">
                            <h2 className="card-title">Project description</h2>
                            <hr/>

                            <p>{project.description}</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default TaigaProjectDetails;
